pragma solidity >=0.4.22 <0.6.0;

import "./CertManager.sol";

contract Cert {

     bytes32 public issuedBy;
     bytes32 public issuedTo;
     bytes32 public issuingDate;
     bytes32 public certHash;

     

     constructor (
         bytes32 _issuedBy, bytes32 _issuedTo, bytes32 _issuingDate, bytes32 _certHash, address _CERTMANAGER_CONTRACT) public {
             issuedBy = _issuedBy;
             issuedTo = _issuedTo;
             issuingDate = _issuingDate;
             certHash = _certHash;

             CertManager manager = CertManager(_CERTMANAGER_CONTRACT); 
             manager.storeCertReference(address(this), _issuedBy, _issuedTo, _issuingDate, _certHash);             
         }
         
          function ()  external {
            // If anyone wants to send Ether to this contract, the transaction gets rejected
            revert("no eth accepted");
        }

}
